import React,{ useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';


const Dashboard = () => {
  
    const  [name, setName ] = useState('');
    const  [token, setToken ] = useState('');
    const  [expired, setExpired ] = useState('');
    const navigate = useNavigate()

    useEffect( () => {
        refreshToken();
    }, [])


    const refreshToken = async() => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpired(decoded.exp);
        } catch (error) {
            if(error.response){
                navigate("/");
            }
        }
    }


    const getValueKalkulator = () => {

    }

  return (
    <div>
        <Navbar/>
        <div className='container mt-5'>
            <h1>Welcome Back : { name }</h1>
            <div className='container'>
                <form onSubmit={ getValueKalkulator }>
                    <h1></h1>
                    <div className="field mt-5">
                        <label>Nilai :</label>
                        <input type="number" className='input' placeholder='Masukkan nilai 1'/>
                        <input type="number" className='input mt-2' placeholder='Masukkan nilai 2'/>
                    </div>
                    <div className="field mt-5 select is-danger">
                        <select className='select'>
                            <option value="+">+</option>
                            <option value="-">-</option>
                            <option value="*">*</option>
                            <option value="/">/</option>
                        </select>
                    </div>
                    <div className="field mt-5">
                        <button className='button is-success is-fullwidth'>Lihat hasil</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
  )
}

export default Dashboard
